use intec\core\helpers\ArrayHelper;

$build = $this->getTemplate(true)->getBuild(true);
$properties = $build->getPage()->getProperties();
$mainBlocks = $properties->get('main_blocks');
$component = $this->getComponent(true);
$componentProperties = $component->properties;
$componentCode = 'reviews';

if (ArrayHelper::getValue($mainBlocks, ['active', $componentCode], 'N') === 'N') {
	$this->display = false;
}

switch (ArrayHelper::getValue($mainBlocks, ['templates', $componentCode])) {
  case 'default':
    $this->setStyleBackgroundColor(null);
    break;
}

$component->properties = $componentProperties;

