$componentProperties = $component->properties;
$componentCode = 'categories';

if (ArrayHelper::getValue($mainBlocks, ['active', $componentCode], 'N') === 'N') {
	$this->display = false;
}

switch (ArrayHelper::getValue($mainBlocks, ['templates', $componentCode])) {
  case 'list':
    $componentProperties['GRID_CATALOG_ROOT_SECTIONS_COUNT'] = 3;
    $componentProperties['VIEW'] = 'list';
    break;
  case 'tiles':
    $componentProperties['GRID_CATALOG_ROOT_SECTIONS_COUNT'] = 6;
    $componentProperties['VIEW'] = 'tile2';
    break;
}

$component->properties = $componentProperties;

